INSERT INTO `nilai` (`no`, `thn`, `smt`, `nis`, `nama`, `kls`, `materi`, `angka`, `desk`) VALUES
(3945, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '1.1. IMAN : Pemahaman', 70, 'Ananda dapat memahami materi dengan cukup baik, namun kurang percaya diri dan ragu ketika menyampaikan materi yang sudah disampaikan. Lebih memperhatikan ketika guru menjelaskan agar mendapatkan pemahaman yang lebih baik. '),
(3946, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '1.2. IMAN : Sikap', 78, 'Sikap ananda terhadap guru dan temannya cukup baik, namun belum terbiasa duduk dengan rapih. Namun demikian ananda cukup mudah untuk dinasehati dan diarahkan. '),
(3947, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '2.1. TAUHID', 70, 'Alhamdulillah pemahaman ananda dalam materi tauhid cukup baik tentang penciptaan siang dan malam, fajar dan kekuasaaan Allah terhadap waktu, namun seringkali bingung dalam mengingat ayat - ayat yang berhubungan dengan materi. '),
(3948, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '2.5. SAINS', 75, 'Alhamdulillah ananda dapat memahami materi alam dalam juz 30 dengan cukup baik, seperti proses bagaimana terjadinya siang dan malam, serta ciri-ciri dan perbedaan dari materi yang disampaikan. Namun ananda perlu lebih dilatih dalam menghafal dan mengingat materi yang disampaikan.'),
(3949, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '2.3. BACA TULIS', 87, 'Alhamdulillah kemampuan membaca ananda cukup baik, namun demikian perlu terus berlatih agar semakin terbiasa. Untuk menulis, ananda melakukannya dengan cukup baik dan rapi, namun perlu lebih teliti dalam penulisan. '),
(3950, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '2.4. BERHITUNG', 88, 'Ananda mampu mengoperasikan penjumlahan dan pengurangan dengan cukup baik, namun masih perlu berlatih dalam berhitung dengan teknik simpan pinjam. Lebih ditingkatkan lagi ketelitian dalam berhitung. Adapun ananda mampu membedakan jenis- jenis bangun datar. '),
(3951, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '3.2. KISAH', 75, 'Alhamdulillah ananda dapat memahami kisah dengan cukup baik. Lebih semangat dalam mengamalkan setiap point keteladanan yang didapat dari kisah nabi adam dan keturunannya.'),
(3952, '2021', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 1', '3.6. LIFE SKILL', 75, 'Alhamdulillah ananda cukup antusias dan semangat ketika mengikuti lifeskill dan mampu mengikuti arahan guru dengan baik.'),
(3953, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '1.1. IMAN : Pemahaman', 85, 'Ananda memiliki pemahaman dan kemampuan menjelaskan pelajaran yang difahaminya dengan cukup baik. Namun demikian perlu lebih banyak dialog agar ananda mendapatkan pemahaman yang lebih baik.'),
(3954, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '1.2. IMAN : Sikap', 78, 'Sikap ananda terhadap guru dan temannya cukup baik, hanya saja ketika belajar belum terbiasa duduk rapi dan belum fokus saat guru menjelaskan. Namun demikian ananda mampu menjawab pertanyaan guru dengan baik dan mudah dinasehati. '),
(3955, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '2.1. TAUHID', 85, 'Alhamdulillah pemahaman ananda dalam materi tauhid tentang penciptaan siang, malam dan kekuasaan Allah tentang waktu cukup baik.'),
(3956, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '2.5. SAINS', 85, 'Alhamdulillah ananda dapat memahami materi alam dalam juz 30 dengan cukup baik, seperti proses bagaimana terjadinya siang dan malam, serta ciri-ciri dan perbedaannya. Namun ananda perlu lebih dilatih dalam mengafal dan mengingat ayat yang berhubungan dengan materi.'),
(3957, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '2.3. BACA TULIS', 83, 'Alhamdulillah kemampuan dalam membaca ananda cukup baik, namun perlu terus berlatih agar semakin terbiasa. Mampu menuliskan kata dan kalimat dengan tanpa bantuan, namun dalam tulisan perlu lebih teliti dan dirapihkan. '),
(3958, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '2.4. BERHITUNG', 85, 'Ananda memiliki kemampuan yang baik dalam berhitung, serta mampu mengerjakan penjumlahan & pengurangan dengan cukup baik. Mampu membedakan bentuk bangun datar dan mengklasifikasi dengan contoh benda yang berhubungan disekitarnya. Namun perlu lebih teliti saat mengerjakan dan kurangi untuk tidak mudah mengeluh diawal'),
(3959, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '3.2. KISAH', 80, 'Alhamdulillah ananda dapat memahami materi kisah nabi adam dengan cukup baik. Cukup mampu menyampaikan kembali point keteladanan dari kisah nabi dan keturunannya. '),
(3960, '2021', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 1', '3.6. LIFE SKILL', 75, 'Alhamdulillah ananda cukup antusias dan semangat ketika mengikuti lifeskill dan mampu mengikuti arahan guru dengan cukup baik.'),
(3961, '2021', '1', '211107', 'Muhammad Kynan Delwyn Alhaq', 'Awal 1', '1.1. IMAN : Pemahaman', 73, 'Ananda memiliki pemahaman yang cukup baik, namun kurang percaya diri ketika diminta menjelaskan atau menjawab pertanyaan. Fokusnya mudah teralih, perbanyak dialog iman bersama orangtua. '),
(3962, '2021', '1', '211107', 'Muhammad Kynan Delwyn Alhaq', 'Awal 1', '1.2. IMAN : Sikap', 75, 'sikap ananda terhadap guru dan temannya cukup baik, hanya saja ketika belajar belum bisa fokus saat guru menjelaskan. Namun cukup mudah dinasihati dan diarahkan.'),
(3963, '2021', '1', '211107', 'Muhammad Kynan Delwyn Alhaq', 'Awal 1', '2.1. TAUHID', 75, 'Alhamdulillah pemahaman ananda dalam materi tauhid tentang penciptaan siang dan malam cukup baik, lebih Semangat dalam belajar  dan mengulang pelajaran agar mendapatkan hasil yang lebih baik.'),
