INSERT INTO `nilai` (`no`, `thn`, `smt`, `nis`, `nama`, `kls`, `materi`, `angka`, `desk`) VALUES
(8951, '2022', '1', '221107', 'Kenzo Muhammad Athalla', 'Awal 1', '1.2. IMAN : Sikap', 70, 'Alhamdulillah ananda cukup mampu bersikap baik kepada guru dan teman -temannya, hanya saja perlu diingatkan untuk fokus.'),
(8952, '2022', '1', '221108', 'Khalifah Umara AlKiraami', 'Awal 1', '1.2. IMAN : Sikap', 65, 'Alhamdulillah ananda cukup mampu bersikap baik kepada guru dan teman temannya, hanya saja ketika materi disampaikan ananda belum bisa fokus dan sering mengobrol.'),
(8953, '2022', '1', '221109', 'Muhammad Al Fatih', 'Awal 1', '1.2. IMAN : Sikap', 80, 'Alhamdulillah ananda mampu bersikap baik kepada guru dan teman temannya, senang mengajak temannya yang sedang kurang bersemangat belajar, hanya saja terkadang belum bisa fokus ketika materi disampaikan.'),
(8954, '2022', '1', '200110', 'Raisa Saffana Ghaisani', 'Awal 3', '2.3. BACA TULIS', 73, 'Ananda dapat membuat kalimat cukup baik, namun kurang memahami bagaimana penulisan paragraf dan penempatan tanda baca dengan baik. Tingkatkan kerapian dalam penulisan!'),
(8955, '2022', '1', '221110', 'Muhammad Shamil Basayev', 'Awal 1', '1.2. IMAN : Sikap', 75, 'Alhamdulillah ananda cukup mampu bersikap baik kepada guru dan teman temannya, hanya saja terkadang terbawa oleh teman, selalu berusaha ketika belajar dan semangat belajar ananda cukup baik.'),
(8957, '2022', '1', '220101', 'Alya Khansa Shakila', 'Awal 1', '2.5. SAINS', 70, 'Alhamdulillah Ananda cukup mampu memahami materi, dan cukup mampu menjawab pertanyaan, hanya saja ada materi yang kurang di pahami yaitu proses terjadinya siang dan malam.'),
(8958, '2022', '1', '220102', 'Aqila Sohwatul Islam', 'Awal 1', '2.5. SAINS', 80, 'Alhamdulillah Ananda mampu memahami materi dengan baik, dan mampu menjawab pertanyaan dengan baik.'),
(8959, '2022', '1', '221111', 'Nahdhan Dzu Ilmin Karim', 'Awal 1', '1.2. IMAN : Sikap', 50, 'Ananda kurang mampu bersikap baik kepada guru dan teman temannya, perlu banyak bimbingan dalam adab majlis, kurang fokus ketika materi disampaikan.'),
(8960, '2022', '1', '220103', 'Arinii Aina EL-Mardhiyyah', 'Awal 1', '2.5. SAINS', 90, 'Ananda  mampu memahami materi dengan sangat baik, ananda selalu antusias ketika guru menjelaskan materi dan mampu menjawab pertanyaan dengan sangat baik.'),
(8961, '2022', '1', '220112', 'Rhea Latisha Zea Nugroho', 'Awal 1', '2.5. SAINS', 70, 'Alhamdulillah ananda cukup mampu memahami materi tentang terjadinya siang dan malam, ananda mampu menghafal materi dengan cukup baik.'),
(8962, '2022', '1', '220113', 'Tsabita Khaylila Mumtazah', 'Awal 1', '2.5. SAINS', 90, 'Alhamdulillah ananda mampu memahami materi tentang terjadinya siang dan malam dengan sangat baik, ananda mampu menjawab pertanyaan dengan sangat baik. '),
(8963, '2022', '1', '220114', 'Thalitsa Humairah Aisha', 'Awal 1', '2.5. SAINS', 70, 'Ananda cukup mampu memahami materi tentang terjadinya siang dan malam, mampu menjawab pertanyaan dengan cukup baik namun terkadang kurang fokus ketika materi disampaikan.'),
(8964, '2022', '1', '220112', 'Rhea Latisha Zea Nugroho', 'Awal 1', '1.5. ALQURAN : Tahsin', 73, 'Ananda mampu membaca Al-Qur\'an dengan cukup baik, hqnya saja masih ada beberapa huruf yang tertukar pembacaannya, dan masih tergesa-gesa. Harus lebih diperhatikan lagi panjang pendeknya.'),
(8965, '2022', '1', '210126', 'Hiatari Athayya Zaviera', 'Awal 2', '2.5. SAINS', 75, 'Ananda mampu memahami materi pembagian hewan berdasarkan makanan dengan baik hanya saja ananda harus lebih sering murojaah karena cepat lupa.'),
(8966, '2022', '1', '210109', 'Khalila Inara Putri', 'Awal 2', '2.5. SAINS', 85, 'Ananda sangat mampu memahami materi proses pertumbuhan tumbuhan, pembagian hewan berdasarkan makanannya, dan ciri-ciri hewan mamalia dengan baik.'),
(8967, '2022', '1', '210121', 'Nadhifa Luthfiatun Nisa', 'Awal 2', '2.5. SAINS', 75, 'Ananda mampu menghafal materi macam-macam energi, proses pertumbuhan tumbuhan dan pembagian hewan berdasarkan makanannya dengan baik. Namun kurang memahami materi permukaan bumi.'),
(8968, '2022', '1', '210105', 'Malaika Elshanum Almaira Asy-syarif', 'Awal 2', '2.5. SAINS', 75, 'Ananda mampu menghafal materi dengan cepat. Memahami materi energi dan makhluk hidup dengan cukup baik, hanya saja masih kurang pada materi permukaan bumi.'),
(8969, '2022', '1', '210119', 'Sanabel Raisan Bilqis', 'Awal 2', '2.5. SAINS', 70, 'Ananda mampu menghafal dan memahami materi energi, permukaan bumi, dan makhluk hidup dengan cukup baik. Hanya saja masih suka keliru dalam menghafal pembagian hewan berdasarkan makanannya, ciri-ciri mamalia, dan ciri-ciri karnivora.'),
(8970, '2022', '1', '210128', 'Wafaa Amaliya El Faaizah', 'Awal 2', '2.5. SAINS', 75, 'Ananda memahami materi macam-macam energi, pertumbuhan tumbuhan, dan macam-macam hewan dengan cukup baik. Hanya saja pada materi permukaan bumi belum menguasai.'),
